pragma solidity ^0.5.0;

import "openzeppelin-solidity/contracts/math/SafeMath.sol";
import "openzeppelin-solidity/contracts/ownership/Secondary.sol";

 /**
  * @title Escrow
  * @dev Base escrow contract for Hero loan tokens, holds funds designated for a loan until they
  * withdraw them or they are put into a succesful loan.
  */
contract LoanEscrow is Secondary {
    using SafeMath for uint256;

    event Deposited(address indexed payee, uint256 weiAmount);
    event Withdrawn(address indexed payee, uint256 weiAmount);
    event WithdrawnForLoan(address indexed payee, uint256 weiAmount);

    mapping(address => uint256) private _deposits;

    function depositsOf(address payee) public view returns (uint256) {
        return _deposits[payee];
    }

    /**
     * @dev Stores the sent amount as credit to be withdrawn.
     * @param payee The destination address of the funds.
     */
    function deposit(address payee) public onlyPrimary payable {
        uint256 amount = msg.value;
        _deposits[payee] = _deposits[payee].add(amount);

        emit Deposited(payee, amount);
    }

    /**
     * @dev Withdraw balance for a payee.
     * @param payee The address whose funds will be withdrawn and transferred to.
     */
    function withdraw(address payable payee, uint256 amount) public onlyPrimary {
        require(amount <= _deposits[payee], "the withdrawal amount must be smaller than the escrow balance");
        uint256 remainingBalance = _deposits[payee].sub(amount);

        _deposits[payee] = remainingBalance;

        payee.transfer(amount);

        emit Withdrawn(payee, amount);
    }

    /**
     * @dev Move balances to the owner for use for loans.
     * @param payee The address whose funds will be withdrawn and transferred to the primary contract.
     */
    function withdrawToContract(address payable payee, uint256 amount) public onlyPrimary {
        require(amount <= _deposits[payee], "the withdrawal amount must be smaller than the escrow balance");
        uint256 remainingBalance = _deposits[payee].sub(amount);

        _deposits[payee] = remainingBalance;

        msg.sender.transfer(amount);
        emit WithdrawnForLoan(payee, amount);
    }
}

